import java.io.*;
import java.math.BigInteger;
import java.text.CollationElementIterator;
import java.util.*;

public class CodeForces {
    private final BufferedReader reader;
    private final PrintWriter writer;
    private StringTokenizer tokenizer;

    private void solve() {
        int n = nextInt();
        boolean[] a = new boolean[n];
        String in = next();
        int res, res2;
        int count = 0;
        int rCount1 = 0;
        int rCount2 = 0;
        int bCount1 = 0;
        int bCount2 = 0;
        for (int i = 0; i < n; i++) {
            if (a[i] = in.charAt(i) == 'r') {
                rCount1++;
                rCount2++;
            }
            else {
                bCount1++;
                bCount2++;
            }
        }
        for (int i = 0; i < n; i++) {
            if ((i % 2 == 0 && a[i]) || (i % 2 == 1 && !a[i])) count++;
            else {
                if (a[i]) rCount1--;
                else bCount1--;
            }
        }
        res = Math.max(0, count - 2 * Math.min(rCount1, bCount1));
        res += ((count - res) / 2);
        count = 0;
        for (int i = 0; i < n; i++) {
            if ((i % 2 == 0 && !a[i]) || (i % 2 == 1 && a[i])) count++;
            else {
                if (a[i]) rCount2--;
                else bCount2--;
            }
        }
        res2 = Math.max(0, count - 2 * Math.min(rCount2, bCount2));
        res2 += ((count - res2) / 2);
        writer.print(Math.min(res, res2));
    }

    public static void main(String[] args) {
        new CodeForces(System.in, System.out);
    }

    private CodeForces(InputStream inputStream, OutputStream outputStream) {
        this.reader = new BufferedReader(new InputStreamReader(inputStream));
        this.writer = new PrintWriter(outputStream);
        solve();
        writer.close();
    }

    private String next() {
        while (tokenizer == null || !tokenizer.hasMoreTokens()) {
            tokenizer = new StringTokenizer(readLine());
        }
        return tokenizer.nextToken();
    }

    private String readLine() {
        String line;
        try {
            line = reader.readLine();
        } catch (IOException e) {
            throw new RuntimeException(e);
        }
        return line;
    }

    private int nextInt() {
        return Integer.parseInt(next());
    }

    private long nextLong() {
        return Long.parseLong(next());
    }

    private double nextDouble() {
        return Double.parseDouble(next());
    }

    public boolean hasNext() {
        while (tokenizer == null || !tokenizer.hasMoreTokens()) {
            String line = readLine();
            if (line == null) {
                return false;
            }
            tokenizer = new StringTokenizer(line);
        }
        return true;
    }

    public BigInteger nextBigInteger() {
        return new BigInteger(next());
    }

    public static int upperBinarySearch(int[] array, int start, int finish, int key) {
        int low = 0;
        int high = array.length - 1;
        if (high < 0) return -1;
        while (low < high) {
            int mid = (low + high + 1) >>> 1;
            if (array[mid] <= key) low = mid;
            else high = mid - 1;
        }
        if (array[low] != key) {
            if (array[low] < key) low++;
            low = -(low + 1);
        }
        return low;
    }
}

