		int m_b = 0, m_r = 0;
		// rbrbrbrb
		for(int i = 0;i < n;i++) {
			if(i % 2 != 0) {
				if(a[i] != 'b')
					m_r++;
			} else {
				if(a[i] != 'r')
					m_b++;
			}
		}

		return Math.max(m_b, m_r);
	}


	static void Arrayssort(int[] a) {
		int n = a.length;// shuffle, then sort
		for (int i = 0; i < n; i++) {
			int oi = random.nextInt(n);
			int temp = a[oi];
			a[oi] = a[i];
			a[i] = temp;
		}
		java.util.Arrays.sort(a);
	}

