         	// Returns count of minimum squares that sum to n
         	static int getMinSquares(int n)
         	{
         	    int dp[]=new int[n+1];
         	    dp[0]=0;
         	    dp[1]=1;
         	    dp[2]=2;
         	    dp[3]=3;

         	    for(int i=4;i<=n;i++)
         	    {
         	        dp[i]=i;
         	        for(int x=1;x*x<=i;x++)
         	        {
         	            if(dp[i]<1+dp[i-x*x])
         	            {
         	                dp[i]=1+dp[i-x*x];
         	            }
         	        }
         	    }

         	    return dp[n];
         	}
        }


