using namespace std;
int calc(char l, string t) {
  int r = 0, b = 0;
  int k = 0, n = t.length();
  for (int i = 0; i < n; i++) {
    if (l == t[i]) {
      if (l == 'r') {
        l = 'b';
        r++;
      } else {
        l = 'r';
        b++;
      }
      k++;
    } else
      l = t[i];
  }
  if (min(b, r) == 0) return k;
  int c = min(b, r);
  if (c * 2 >= k) return k / 2 + k % 2;
  k -= c * 2;
  return c + k;
}
