			if (roaches[c] != current) counts1[current]++;
			if (roaches[c] != next) counts2[next]++;
			char temp = current;
			current = next;
			next = temp;
		}
		current = 'b'; next = 'r';
		int min = Integer.MAX_VALUE;

		int larger = Math.max(counts1[current], counts1[next]);
		int smaller =  counts1[current] + counts1[next] - larger;
		int diff = larger - smaller;
		min = Math.min(smaller+diff, min);
		char temp = current;
		current = next;
		next = temp;

		larger = Math.max(counts2[current], counts2[next]);
		smaller =  counts2[current] + counts2[next] - larger;
		diff = larger - smaller;
		min = Math.min(smaller+diff, min);
		temp = current;
		current = next;
		next = temp;

		System.out.println(min);
	}

}

