        int countNext = 0;
        for (int i = 0; i < num; i++) {
            if (i % 2 == 0 && line[i] == next) {
                countBegin++;
            }else if(i % 2 != 0 && line[i] == begin){
                countNext++;
            }
        }
        if(countBegin > countNext) return countBegin;
        else return countNext;
