	}
	private static int answer (char black, char red, char[] s) {
		int answer = 0;
		int b = 0; int r = 0;
		int bwp = 0; int rwp = 0;
		for (int i = 0; i < s.length; i++) {
			if (s[i]==black) {
				b++;
			} else {
				r++;
			}
			char c = (i%2==0)?black:red;
			if (s[i]!=c){
				if (s[i]==black) {
					bwp++;
				} else {
					rwp++;
				}
			}
		}
		if (r < s.length/2) {
			answer = s.length/2 - r + rwp;
		} else {
			answer = r - s.length/2 + bwp;
		}
		return answer;
	}

