string make(int N, char C) {
  string S;
  S += C;
  for (int i = 1; i < N; i++) {
    if (S[i - 1] == 'r')
      S += 'b';
    else
      S += 'r';
  }
  return S;
}
int check(string S, string S1, int N) {
  int R = 0, B = 0;
  for (int i = 0; i < N; i++) {
    if (S[i] != S1[i]) {
      if (S1[i] == 'r')
        R++;
      else
        B++;
    }
  }
  return max(R, B);
}
