public static int BinarySearch(int x,int a[])
{
	int l=0;
	int r=a.length-1;
	int temp=-1;
	while(l<=r)
	{
		int mid=l+(r-l)/2;
		if(a[mid]<x)
		{
			temp=mid;
			l=mid+1;
		}
		else if(a[mid]>x)
			r=mid-1;
		else if(a[mid]<x)
			l=mid+1;

	}
	return temp;
}
