    static class BAnatolyAndCockroaches {
        public void solve(int testNumber, inclass in, PrintWriter out) {
            int n = in.nextInt();
            String str = in.readLine();
            StringBuilder sb1 = new StringBuilder();
            StringBuilder sb2 = new StringBuilder();
            for (int i = 0; i < n; i++) {
                if (i % 2 == 0) {
