            String ans2 = sb2.toString();
            int min_color_change = 0;
            int c1 = 0, c2 = 0;
            for (int i = 0; i < n; i++) {
                if (str.charAt(i) != ans1.charAt(i)) {
                    c1++;
                }
                if (str.charAt(i) != ans2.charAt(i)) {
                    c2++;
                }
            }
            min_color_change = Math.min(c1, c2);
            int[] arr1 = new int[2], arr2 = new int[2];
            for (int i = 0; i < n; i++) {
                if (str.charAt(i) != ans1.charAt(i)) {
                    arr1[str.charAt(i) == 'b' ? 0 : 1]++;
