                ns = wr<wb?wr:wb;
                nr = wr+wb-ns-ns;
            }
            else
            {
                // start with r

                int wb=0,wr=0;
                for(int j=0;j<s.length();j++)
                {
                    char ch = s.charAt(j);

                    if(j%2==0 && ch=='b')
                        wb++;
                    if(j%2!=0 && ch=='r')
                        wr++;
                }
                ns = wr<wb?wr:wb;
                nr = wr+wb-ns-ns;
            }
        System.out.println(ns+nr);

    }
}

