    public void solve(int taskNumber, InputReaderCockroaches in, PrintWriter out) {
        int n = in.nextInt();
        String s = in.next();
        int a, b, A, B;
        a = b = A = B = 0;
        for(int i = 0; i < n; ++i){
            if(i % 2 == 0 && s.charAt(i) == 'r'){
                a++;
            }
            if(i % 2 == 1 && s.charAt(i) == 'b'){
                b++;
            }
            if(i % 2 == 0 && s.charAt(i) == 'b'){
                B++;
            }
            if(i % 2 == 1 && s.charAt(i) == 'r'){
                A++;
            }
        }
        int first = Math.min(a, b) + Math.max(a, b) - Math.min(a, b);
        int second = Math.min(A, B) + Math.max(A, B) - Math.min(A, B);
        System.out.println(Math.min(first, second));
    }
}

class InputReaderCockroaches {
