long long C(int k, int l) {
  if (l == k) return 1;
  if (l * 2 > k) l = k - l;
  long long res = 1, temp = l;
  for (int i = k - l + 1; i <= k; i++) {
    res *= i / temp;
    temp--;
  }
  return res;
}
long long sumOfDigit(int x) {
  int res = 0;
  while (x) {
    res += x % 10;
    x /= 10;
  }
  return res;
}
