void SieveOfEratosthenes(int n) {
  soso.push_back(2);
  for (int p = 3; p * p <= n; p += 2) {
    if (prime[p] == false) {
      for (int i = p * 2; i <= n; i += p) prime[i] = true;
    }
  }
  for (int i = 3; i <= n; i += 2)
    if (!prime[i]) soso.push_back(i);
}
