     //constructing segment tree
    private void constructseg(long[] seg, long[] input, int p, int q, int pos) {
        if(p==q)
        {
            seg[pos]=input[p];
            return;
        }
        int mid=(q+p)/2;
        constructseg(seg,input,p,mid,2*pos+1);
        constructseg(seg,input,mid+1,q,2*pos+2);
        seg[pos]=seg[2*pos+1]+seg[2*pos+2];
    }


