    private ArrayList<Integer> minFactor(int a) {
        ArrayList<Integer> obj=new ArrayList<>();
        for(int i=2;i<=a;i++)
        {
            while(a!=0&&a%i==0)
            {
                obj.add(i);
                a/=i;
            }
        }
        return obj;
    }


    // check whether the number is prime or not
    public static boolean isPrimeNumber(int i) {
        int factors = 0;
        int j = 1;

        while(j <= i)
        {
            if(i % j == 0)
            {
                factors++;
            }
            j++;
        }
        return (factors == 2);
    }

