    if (i % 2 == 1 && s[i] != 'r') {
      l2++;
    } else if (i % 2 == 0 && s[i] != 'b') {
      l2++;
    }
  }
  long long ans = 0, ans1 = 0, ans2 = 0, ans3 = 0;
  for (long long i = 0; i < n; i++) {
    if (i % 2 == 0) {
      if (s[i] == 'r') {
        continue;
      } else {
        ans++;
      }
    } else {
      if (s[i] == 'b') {
        continue;
      } else {
        ans1++;
      }
    }
  }
  for (long long i = 0; i < n; i++) {
    if (i % 2 == 1) {
