      ++diff;
      if (a[i] == 'r')
        ++ra;
      else
        ++rc;
    }
    t = (t + 1) % 2;
  }
  d = abs(ra - rc);
  ans2 = d + (diff - d) / 2;
  printf("%d\n", min(ans1, ans2));
