def do_solve(n, A, B):
    d = {1: 0, -1: 0}
    for i in xrange(n):
        a, b = A[i], B[i]
        if a == b:
            continue
        d[a - b] = d.get(a - b, 0) + 1
    return min(d[-1], d[1]) + abs(d[-1] - d[1])

def solve(n, ns):
    ns = map(lambda x: {'r': 0, 'b': 1}[x], ns)
    A = [i % 2 for i in xrange(n)]
    B = [(i + 1) % 2 for i in xrange(n)]
    return min(do_solve(n, ns, A), do_solve(n, ns, B))

assert(solve(5, 'rbbrr') == 1)
assert(solve(5, 'bbbbb') == 2)
assert(solve(3, 'rbr') == 0)
assert(solve(3, 'brb') == 0)
assert(solve(1, 'b') == 0)
assert(solve(1, 'r') == 0)

