def calc(l, c):
    c1 = 'b'
    if c == 'b':
        c1 = 'r'
    s = list()
    for i in xrange(len(l)):
        if i % 2 == 0:
            s.append(c)
        else:
            s.append(c1)
    r, c = 0, 0
    for i in xrange(len(l)):
        if s[i] == l[i]:
            continue
        elif s[i] == 'r':
            r += 1
        else:
            c += 1
    return max(r, c)

