                if(roachLine[i] != (i % 2 == 0))
                {
                    if(roachLine[i])
                        blackBufferT++;
                    else
                        redBufferT++;
                }else if(roachLine[i] != (i % 2 == 1)){
                    if(!roachLine[i])
                        blackBufferF++;
                    else
                        redBufferF++;
                }
            }
        }

        int roundsF = Math.min(blackBufferF, redBufferF);
        int roundsT = Math.min(blackBufferT, redBufferT);

        roundsF += blackBufferF - Math.min(blackBufferF, redBufferF);
