            } catch (IOException e) { 
                e.printStackTrace(); 
            } 
            return str; 
        } 
    }

    static int gcd(int a, int b) {
        int r = a % b;
        while (r != 0) {
            a = b; b = r;
            r = a % b;
        }
        return b;
    }

    static int position(char[] str, int r, int b) {
        int p = 0, q = 0;
        for (int i = 0; i < str.length; i += 1) {
            if (i % 2 == r && str[i] == 'r') p += 1;
            if (i % 2 == b && str[i] == 'b') q += 1;
        }
        return Math.min(p, q) + Math.abs(p - q);
    }

    public static void main(String[] args) { 
        FastReader fr = new FastReader(); 
        PrintWriter out = null;
