    private void solve() throws IOException
    {

    	int n = nextInt();

    	String s = nextToken();

    	int ans1 = 0, ans2 = 0;
    	int bCount = 0, rCount = 0;
    	for (int i = 0; i < s.length(); i++) {
    		char c = s.charAt(i);

    		if (i%2 == 0 && c == 'r')
    			rCount++;
    		else if (i%2==1 && c == 'b')
    			bCount++;

    		int swap = Math.min(rCount, bCount);
    		int color = Math.max(rCount, bCount) - swap;

    		ans1 = swap + color;

    	}

    	bCount = 0;
    	rCount = 0;
    	for (int i = 0; i < s.length(); i++) {
    		char c = s.charAt(i);

    		if (i%2 == 0 && c == 'b')
    			bCount++;
    		else if (i%2==1 && c == 'r')
    			rCount++;

    		int swap = Math.min(rCount, bCount);
    		int color = Math.max(rCount, bCount) - swap;

    		ans2 = swap + color;

    	}

    	writer.print(Math.min(ans1,ans2));


    }

