import java.util.Scanner;

/**
 * Built using CHelper plug-in
 * Actual solution is at the top
 */
public class Main {
    public static void main(String[] args) {
        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        Scanner in = new Scanner(inputStream);
        PrintWriter out = new PrintWriter(outputStream);
        TaskCF373D2B solver = new TaskCF373D2B();
        solver.solve(1, in, out);
        out.close();
    }

    static class TaskCF373D2B {
        int solve(char[] c, char fc) {
            char[] end = new char[c.length];

            end[0] = fc;
            for (int i = 1; i < end.length; i++) {
                char prev = end[i - 1];

                end[i] = (end[i - 1] == 'b') ? 'r' : 'b';
            }


            int same, btr, rtb;
            same = btr = rtb = 0;

            for (int i = 0; i < c.length; i++) {
                if (c[i] == end[i]) {
                    same++;
                } else if (c[i] == 'b') {
                    btr++;
                } else {
                    rtb++;
                }
            }

            return Math.max(btr, rtb);
        }

        public void solve(int testNumber, Scanner in, PrintWriter out) {
            int n = in.nextInt();

            char[] c = in.next().toCharArray();

            int turns = solve(c, 'b');

            turns = Math.min(turns, solve(c, 'r'));

            out.println(turns);
        }

    }
}

import java.util.Scanner;

public class Main {

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        String str = sc.next();

        int rEven = 0;
        int rOdd = 0;
        int bEven = 0;
        int bOdd = 0;
        for (int i = 0; i < n; i++) {
            char color = str.charAt(i);
            if(color == 'r'){
                rEven += Math.abs(i % 2 - 1);
                rOdd += i % 2;
            } else {
                bEven += Math.abs(i % 2 - 1);
                bOdd += i % 2;
            }
        }

        int result = 0;
//        p("rEven " + rEven + " bOdd " + bOdd + " bEven " + bEven + " rOdd " + rOdd);
        if(rEven + bOdd > rOdd + bEven){
            result += Math.min(rOdd, bEven) + Math.abs(rOdd - bEven);
        } else {
            result += Math.min(rEven, bOdd) + Math.abs(rEven - bOdd);
        }

        p(result);
    }

    public static void p(Object obj) {
        System.out.println(obj);
    }
}

