		public void solve(InputReader in, PrintWriter out) {

			int x = in.nextInt();
			char[] arr = new char[x];

			String str = in.nextLine();
			for (int i = 0; i < x; i++) {
				arr[i] = str.charAt(i);
			}

			int firstBniye = getFirstB(arr);
			int firstBproblem = getFirstBproblem(firstBniye, x);
			int seconRniye = getSecondR(arr);
			int secondRproblme = getSecondRproblme(seconRniye, x);

			int ans1 = Math.max(firstBproblem, secondRproblme);

			int firstRniye = getFirstR(arr);
			int firstRproblem = getFirstRproblem(firstRniye, x);
			int seconBniye = getSecondB(arr);
			int secondBproblme = getSecondBproblme(seconBniye, x);

			int ans2 = Math.max(firstRproblem, secondBproblme);

			int ans = Math.min(ans1, ans2);
			out.println(ans);

		}

		private int getSecondBproblme(int seconBniye, int x) {
			int totalHobe = x / 2;
			return totalHobe - seconBniye;
		}

