			int x = 0;
			for (int i = 1; i < arr.length; i += 2) {
				if (arr[i] == 'b') {
					x++;
				}
			}
			return x;
		}

		private int getFirstRproblem(int firstRniye, int x) {
			int totalHobe = x / 2;
			if (x % 2 == 1) {
				totalHobe++;
			}
			return totalHobe - firstRniye;
		}

		private int getFirstR(char[] arr) {
			int x = 0;
			for (int i = 0; i < arr.length; i += 2) {
