int calc(int color, int x, int y, int n) {
  char start = color ? 'r' : 'b';
  char other = start == 'r' ? 'b' : 'r';
  int needx = n & 1 ? n / 2 + 1 : n / 2;
  int needy = n - needx;
  int x_to_y = max(0, x - needx);
  int y_to_x = max(0, y - needy);
  int wrong = 0;
  int res = 0;
  for (int i = 0; i < n; ++i) {
    if (i & 1) {
      if (s[i] != other) {
        if (x_to_y > 0) {
          --x_to_y;
          ++res;
        } else
          ++wrong;
      }
    } else {
      if (s[i] != start) {
        if (y_to_x > 0) {
          --y_to_x;
          ++res;
        } else
          ++wrong;
      }
    }
  }
  0 ? (cout << "color"
            << " = " << (color) << ","
            << "res"
            << " = " << (res) << ","
            << "wrong"
            << " = " << (wrong) << "\n")
    : cout;
  return res + wrong / 2;
}
