def solve(pole, nxt):
    opposite = {'r': 'b', 'b': 'r'}
    count_not_on_place = {'r': 0, 'b': 0}
    for item in pole:
        if nxt != item:
            count_not_on_place[item] += 1
        nxt = opposite[nxt]

    return max(count_not_on_place.values())


