int case1(string str1, int n) {
  char ch0 = 'r';
  char ch1 = 'b';
  int oddiff = 0, evendiff = 0;
  for (int i = 0; i < n; i++) {
    if (i % 2 == 0) {
      if (str1[i] != ch0) {
        evendiff++;
      }
    } else {
      if (str1[i] != ch1) {
        oddiff++;
      }
    }
  }
  return max(evendiff, oddiff);
}
int case2(string str1, int n) {
  char ch0 = 'b';
  char ch1 = 'r';
  int oddiff = 0, evendiff = 0;
  for (int i = 0; i < n; i++) {
    if (i % 2 == 0) {
      if (str1[i] != ch0) {
        evendiff++;
      }
    } else {
      if (str1[i] != ch1) {
        oddiff++;
      }
    }
  }
  return max(evendiff, oddiff);
}
