def dist(s, choice):
    r_diff = 0
    b_diff = 0
    for i in range(len(s)):
        if choice[i] != s[i]:
            if s[i] == 'r':
                r_diff += 1
            else:
                b_diff += 1
    min_diff = min(r_diff, b_diff)
    return min_diff + (r_diff - min_diff) + (b_diff - min_diff)


