public static void sieveOfEratosthenes() 
    { 
        // FALSE == prime

        // TRUE ==  COMPOSITE

        // FALSE== 1


        // time complexity = 0(NlogLogN)== o(N)

        // gives prime nos bw 1 to N

        for(int i = 4; i<= sieve ; i++)
        {
            prime[i] = true  ;
            i++ ;
        }

        for(int p = 3; p*p <= sieve; p++) 
        { 

            if(prime[p] == false) 
            { 

                for(int i = p*p; i <= sieve; i += p) 
                    prime[i] = true; 
            } 

            p++ ;
        } 




    } 

