/**
 * Built using CHelper plug-in
 * Actual solution is at the top
 *
 * @author Aeroui
 */
public class Main {
    public static void main(String[] args) {
        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        Scanner in = new Scanner(inputStream);
        PrintWriter out = new PrintWriter(outputStream);
        TaskB solver = new TaskB();
        solver.solve(1, in, out);
        out.close();
    }

    static class TaskB {
        public void solve(int testNumber, Scanner in, PrintWriter out) {
            int n = in.nextInt();
            char[] arr = in.next().toCharArray();

            int cntr1 = 0;
            int cntb1 = 0;
            int cntr2 = 0;
            int cntb2 = 0;

            char cur1 = arr[0];
            char cur2 = swap(arr[0]);
            for (int i = 1; i < n; ++i) {

                if (arr[i] == cur1) {
                    if (cur1 == 'r') ++cntb1;
                    else ++cntr1;
                }

                if (arr[i] == cur2) {
                    if (cur2 == 'r') ++cntb2;
                    else ++cntr2;
                }

                cur1 = swap(cur1);
                cur2 = swap(cur2);
            }

            if (arr[0] == 'r') cntb2++;
            else cntr2++;

            int ans1 = Math.min(cntr1, cntb1);
            int ans2 = Math.min(cntr2, cntb2);
            cntr1 -= ans1;
            cntb1 -= ans1;
            cntr2 -= ans2;
            cntb2 -= ans2;
            ans1 += cntr1 + cntb1;
            ans2 += cntr2 + cntb2;

            out.println(Math.min(ans1, ans2));
        }

        char swap(char c) {
            if (c == 'r') return 'b';
            return 'r';
        }

    }
}

