            if(current==actual[i]){
                if(current){
                    goodTrues++;
                }else{
                    goodFalses++;
                }
            }else{
                if(current){
                    badFalses++;
                }else{
                    badTrues++;
                }
            }
        }

        int swaps=Math.min(badFalses,badTrues);
        int changes=badFalses+badTrues-2*swaps;
        return changes+swaps;
    }
}

