def solve(line):
    length = len(line)
    if length < 2:
        return 0

    diff = [0,0]
    color = [line[0], 'b']
    if line[0] == 'r':
        color[1] = 'b'
    else:
        color[1] = 'r'
    color2 = [color[1], color[0]]
    diff2 = [0,0]

    for i, c in enumerate(line):
        idx = i % 2
        if c != color[idx]:
            diff[idx]+=1
        if c != color2[idx]:
            diff2[idx]+=1

    return min([max(diff), max(diff2)])


