		char[] X1 = new char[C.length];
		for (int i = 0; i < C.length; i += 2) {
			X1[i] = 'r';
			if (i != C.length - 1)
				X1[i + 1] = 'b';
		}
		char[] X2 = new char[C.length];
		for (int i = 0; i < C.length; i += 2) {
			X2[i] = 'b';
			if (i != C.length - 1)
				X2[i + 1] = 'r';
		}
		int a = 0, b = 0;
		for (int i = 0; i < C.length; i += 2) {
			if (C[i] != X1[i])
				a++;

			if (i != C.length - 1)
				if (C[i+1] != X1[i + 1])
					b++;

		}
		time1 = a > b ? a : b;
		a = 0;
		b = 0;
		for (int i = 0; i < C.length; i += 2) {
			if (C[i] != X2[i])
				a++;

			if (i != C.length - 1)
				if (C[i+1] != X2[i + 1])
					b++;

		}
		time2 = a > b ? a : b;
		if (time1 > time2)
			System.out.println(time2);
		else
			System.out.println(time1);
	}

