def f(first, s):
    second = 'r' if first == 'b' else 'b'
    alt = [first, second]

    error = [0, 0]
    for i,ch in enumerate(s):
        shouldbe = alt[i % 2]
        if ch != shouldbe:
            error[i % 2] += 1

    return max(error)

