		//readNextLine();
		int T=1;//NextInt();
		StringBuilder sb = new StringBuilder();
		for(int t=1; t<=T; t++)
		{
			readNextLine();
			int N=NextInt();
			readNextLine();
			boolean [] a = new boolean[N];
			for(int i=0; i<N; i++)
