				a[i] = line.charAt(i)=='b';
			}
			int r=0, b=0;
			for(int i=0; i<N; i++)
			{
				if(a[i++])r++;
				if(i<N&&!a[i])b++;
			}
			int ret = Math.min(r, b);
			r-=ret;
			b-=ret;
