    public static int calcLine(char[] A, char[] ord, int N) {
    	int rOff = 0;
    	int bOff = 0;
    	for (int i = 0; i < N; i++) {
    		if (A[i] == 'r' && ord[i % 2] != 'r') {
    			rOff++;
    		}
    		if (A[i] == 'b' && ord[i % 2] != 'b') {
    			bOff++;
    		}
    	}
    	return Math.max(rOff, bOff);
    }

