import java.io.*;
import java.util.*;

public class Solution {
    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        int N = Integer.parseInt(br.readLine());
        String s = br.readLine();
        System.out.println(solve(N,s));
    }

    private static int solve(int N,String s){
        int min1 = 0,min2 = 0;
        char x = 'b';
        int b1 = 0 , r1 = 0;
        for(int i=0;i<N;++i){
            if(s.charAt(i) != x){
                min1++;
                if(s.charAt(i) == 'b') b1++;
                else r1++;
            }
            x = x == 'b' ? 'r' : 'b';
        }

        x = 'r';
        int b2 = 0,r2 = 0;
        for(int i=0;i<N;++i){
            if(s.charAt(i) != x){
                min2++;
                if(s.charAt(i) == 'b') b2++;
                else r2++;
            }
            x = x == 'b' ? 'r' : 'b';
        }

        int min3 = Math.min(b1,r1) + (Math.max(b1,r1) - Math.min(b1,r1));       
        int min4 = Math.min(b2,r2) + (Math.max(b2,r2) - Math.min(b2,r2));

        return Math.min(Math.min(min1,min2),Math.min(min3,min4));
    }
}

