            if(i%2 == 0) {
                if(ch == 'r') {
                    evenR++;
                } else {
                    evenB++;
                }
            } else {
                if(ch == 'r') {
                    oddR++;
                } else {
                    oddB++;
                }
            }
        }
        int redCt = Math.max(oddR, evenB);
        int blueCt = Math.max(oddB, evenR);
        count = Math.min(redCt, blueCt);
        diff = Math.abs((oddR+ oddB - evenR - evenB));
        count = count + diff/2;
