    cout << n / 2 << "\n";
    return;
  }
  long long int cr = 0, cb = 0;
  for (i = 0; i < n; i++) {
    if (i % 2 == 0)
      if (s[i] == 'r') cr++;
    if (i % 2 != 0)
      if (s[i] == 'b') cb++;
  }
  ans = max(cr, cb);
  cr = 0, cb = 0;
  for (i = 0; i < n; i++) {
    if (i % 2 != 0)
      if (s[i] == 'r') cr++;
    if (i % 2 == 0)
      if (s[i] == 'b') cb++;
  }
  ans = min(ans, max(cr, cb));
  cout << ans << "\n";
}
