import java.util.Scanner;

public class A787 {

	public static void main(String[] args) {

		Scanner s = new Scanner(System.in);
		int a = s.nextInt();
		int b = s.nextInt();
		int c = s.nextInt();
		int d = s.nextInt();
		boolean[] reached = new boolean[20000];
		int k = 0;
		while (b + k * a < 20000) {
			reached[b + k * a] = true;
			k++;
		}
		k = 0;
		boolean found = false;
		while (d + k * c < 20000) {
			if (reached[d + k * c]) {
				System.out.println(d + k * c);
				found = true;
				break;
			}
			k++;
		}
		if (!found) System.out.println(-1);

	}

}

import java.io.*;
import java.util.*;

public class A {

    void solve() {
        long a = in.nextInt();
        long b = in.nextInt();
        long c = in.nextInt();
        long d = in.nextInt();
        if (d == b) {
            out.println(b);
            return;
        }
        long res = -1;
        HashSet<Long> was = new HashSet<>();
        was.add(b);
        was.add(d);
        for (int i = 0; i < (int)1e5; i++) {
            b += a;
            d += c;
            if (was.contains(b)) {
                res = b;
                break;
            }
            was.add(b);
            if (was.contains(d)){
                res = d;
                break;
            }
            was.add(d);
        }
        out.println(res);
    }

    FastScanner in;
    PrintWriter out;

    void run() {
        try {
            in = new FastScanner(new File("B.in"));
            out = new PrintWriter(new File("B.out"));

            solve();

            out.close();
        } catch (FileNotFoundException e) {
            e.printStackTrace();
        }
    }

    void runIO() {

        in = new FastScanner(System.in);
        out = new PrintWriter(System.out);

        solve();

        out.close();
    }

    class FastScanner {
        BufferedReader br;
        StringTokenizer st;

        public FastScanner(File f) {
            try {
                br = new BufferedReader(new FileReader(f));
            } catch (FileNotFoundException e) {
                e.printStackTrace();
            }
        }

        public FastScanner(InputStream f) {
            br = new BufferedReader(new InputStreamReader(f));
        }

        String next() {
            while (st == null || !st.hasMoreTokens()) {
                String s = null;
                try {
                    s = br.readLine();
                } catch (IOException e) {
                    e.printStackTrace();
                }
                if (s == null)
                    return null;
                st = new StringTokenizer(s);
            }
            return st.nextToken();
        }

        boolean hasMoreTokens() {
            while (st == null || !st.hasMoreTokens()) {
                String s = null;
                try {
                    s = br.readLine();
                } catch (IOException e) {
                    e.printStackTrace();
                }
                if (s == null)
                    return false;
                st = new StringTokenizer(s);
            }
            return true;
        }

        int nextInt() {
            return Integer.parseInt(next());
        }

        long nextLong() {
            return Long.parseLong(next());
        }

        double nextDouble() {
            return Double.parseDouble(next());
        }
    }

    public static void main(String[] args) {
        new A().runIO();
    }
}

