    printf("-1\n");
  else {
    int b1 = c / g;
    x *= (tmp / g);
    x = (x % b1 + b1) % b1;
    while ((tmp - a * x) / c > 0) x += b1;
    printf("%d\n", a * x + b);
  }
  return 0;
}

