public class Problem implements Runnable {

    private static final boolean ONLINE_JUDGE = System.getProperty("ONLINE_JUDGE") != null;

    private BufferedReader in;
    private PrintWriter out;
    private StringTokenizer tok = new StringTokenizer("");

    private void init() throws FileNotFoundException {
        Locale.setDefault(Locale.US);
        String fileName = "";
        if (ONLINE_JUDGE && fileName.isEmpty()) {
            in = new BufferedReader(new InputStreamReader(System.in));
            out = new PrintWriter(System.out);
        } else {
            if (fileName.isEmpty()) {
                in = new BufferedReader(new FileReader("input.txt"));
                out = new PrintWriter("output.txt");
            } else {
                in = new BufferedReader(new FileReader(fileName + ".in"));
                out = new PrintWriter(fileName + ".out");
            }
        }
    }

    String readString() {
        while (!tok.hasMoreTokens()) {
            try {
                tok = new StringTokenizer(in.readLine());
            } catch (Exception e) {
                return null;
            }
        }
        return tok.nextToken();
    }

    int readInt() {
        return Integer.parseInt(readString());
    }

    int[] readArray(int N) {
        int[] a = new int[N];
        for (int i = 0; i < N; i++)
            a[i] = readInt();
        return a;
    }

    long[] readArrayL(int N) {
        long[] a = new long[N];
        for (int i = 0; i < N; i++)
            a[i] = readLong();
        return a;
    }

    long readLong() {
        return Long.parseLong(readString());
    }

    double readDouble() {
        return Double.parseDouble(readString());
    }

    long gcd(long a, long b) {
        return (b == 0 ? a : gcd(b, a % b));
    }

    int gcd(int a, int b) {
        return (b == 0 ? a : gcd(b, a % b));
    }

    long binpow(long a, long b, long m) {
        if (b == 0) return 1 % m;
        if (b % 2 == 0) {
            long h = binpow(a, b / 2, m);
            return ((h % m) * (h % m)) % m;
        } else
            return (binpow(a, b - 1, m) * a) % m;
    }

    public static void main(String[] args) {
        new Problem().run();
    }

    public void run() {
        try {
            init();
            init();
            solve();
            out.close();
        } catch (Exception e) {
            e.printStackTrace();
            System.exit(-1);
        }
    }
    private void solve() throws IOException {
        long a=readLong(),b=readLong(),c=readLong(),d=readLong();
        out.println(ans(a,b,c,d));
    }
    long ans(long a,long b,long c,long d){
        for (long i=Math.max(d,b);i<=1000000;i++)
            if ((i-b)%a==0&&(i-d)%c==0)
                return i;
        return -1;
    }
}

