/**
 * @author Don Li
 */
public class TheMonster {
    void solve() {
        int a = in.nextInt(), b = in.nextInt();
        int c = in.nextInt(), d = in.nextInt();

        if (b == d) {
            out.println(b);
            return;
        }

        int[] ret = ext_gcd(a, c);

        int gcd = ret[0];
        if ((d - b) % gcd != 0) {
            out.println(-1);
            return;
        }
        int m = (d - b) / gcd;

        int x = m * ret[1], y = m * ret[2];
        while (x < 0 || y > 0) {
            x += c / gcd;
            y -= a / gcd;
        }
        while (x - c / gcd >= 0 && y + a / gcd <= 0) {
            x -= c / gcd;
            y += a / gcd;
        }
        out.println(b + a * x);
    }

    int[] ext_gcd(int a, int b) {
        if (b == 0) return new int[]{a, 1, 0};
        int[] ret = ext_gcd(b, a % b);
        int g = ret[0];
        int x = ret[2];
        int y = ret[1] - a / b * ret[2];
        return new int[]{g, x, y};
    }

    public static void main(String[] args) {
        in = new FastScanner(new BufferedReader(new InputStreamReader(System.in)));
        out = new PrintWriter(System.out);
        new TheMonster().solve();
        out.close();
    }

    static FastScanner in;
    static PrintWriter out;

    static class FastScanner {
        BufferedReader in;
        StringTokenizer st;

        public FastScanner(BufferedReader in) {
            this.in = in;
        }

        public String nextToken() {
            while (st == null || !st.hasMoreTokens()) {
                try {
                    st = new StringTokenizer(in.readLine());
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            return st.nextToken();
        }

        public int nextInt() {
            return Integer.parseInt(nextToken());
        }

        public long nextLong() {
            return Long.parseLong(nextToken());
        }

        public double nextDouble() {
            return Double.parseDouble(nextToken());
        }
    }
}

