    //Scanner in = new Scanner(new BufferedReader(new InputStreamReader(System.in)));

    public static int soln() {
        int a = nI(), b = nI(), c = nI(), d = nI();
        int man = infi;
        for (int i = 0; i < 1000; i++) {
            for (int j = 0; j < 1000; j++) {
                if (b + i * a == d + j * c) {
