def greatestCommonDivisor(a, b):
    if (a == 0 or b == 0):
        return a + b
    while (a != b):
        if (a > b):
            a -= b
        else:
            b -= a
    return a

def firstScreamTime(a, b, c, d):
    bias = d - b
    gcd = greatestCommonDivisor(a, c)
    if (bias % gcd != 0):
        return -1
    y = 0
    while(True):
        x = (bias + c * y) / a
        if (x == x // 1 and x >= 0):
            return y * c + d

        y += 1

