        solver.solve(1, in, out);
        out.close();
    }

    static class TaskA {
        public void solve(int testNumber, InputReader in, PrintWriter out) {
            int a = in.nextInt();
            int b = in.nextInt();
            int c = in.nextInt();
            int d = in.nextInt();
            if (b == d) {
                out.println(b);
                return;
            }
            for (int i = 0; i < 10000; i += 1) {
                if (b == d) break;
                if (b < d) b += a;
                else d += c;
            }
            out.println((b == d) ? b : -1);
        }

    }

